//Write a program that accepts 2 numbers as CLI and print their sum


package milestone1.javafundamentals.languageBasics;

public class SumOfTwoCLI {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Sum of "+ args[0] + " and "+ args[1] +" is "+(Integer.valueOf(args[0])+ Integer.valueOf(args[1])));
	}
}


